pragma solidity ^0.4.2;
// 0v2 JC add wiki to https://github.com/danigrant/gravity
// 0.4.21. ganoche 7545

contract GravatarRegistry {
  event NewGravatar(uint id, address owner, string displayName, string imageUrl, string wiki);
  event UpdatedGravatar(uint id, address owner, string displayName, string imageUrl, string wiki);

  struct Gravatar {
    address owner;
    string displayName;
    string imageUrl;
    string wiki;
  }

  Gravatar[] public gravatars;

  mapping (uint => address) public gravatarToOwner;
  mapping (address => uint) public ownerToGravatar;

  function createGravatar(string _displayName, string _imageUrl, string _wiki) public {
    require(ownerToGravatar[msg.sender] == 0);
    uint id = gravatars.push(Gravatar(msg.sender, _displayName, _imageUrl, _wiki)) - 1;

    gravatarToOwner[id] = msg.sender;
    ownerToGravatar[msg.sender] = id;

    emit NewGravatar(id, msg.sender, _displayName, _imageUrl, _wiki);
  }

  function getGravatar(address owner) public view returns (string, string, string) {
    uint id = ownerToGravatar[owner];
    return (gravatars[id].displayName, gravatars[id].imageUrl, gravatars[id].wiki);
  }

  function updateGravatarName(string _displayName) public {
    require(ownerToGravatar[msg.sender] != 0);
    require(msg.sender == gravatars[ownerToGravatar[msg.sender]].owner);

    uint id = ownerToGravatar[msg.sender];

    gravatars[id].displayName = _displayName;
    emit UpdatedGravatar(id, msg.sender, _displayName, gravatars[id].imageUrl, gravatars[id].wiki);
  }

  function updateGravatarImage(string _imageUrl) public {
    require(ownerToGravatar[msg.sender] != 0);
    require(msg.sender == gravatars[ownerToGravatar[msg.sender]].owner);

    uint id = ownerToGravatar[msg.sender];

    gravatars[id].imageUrl =  _imageUrl;
    emit UpdatedGravatar(id, msg.sender, gravatars[id].displayName, _imageUrl, gravatars[id].wiki);
  }
  function updateGravatarWiki(string _wiki) public {
    require(ownerToGravatar[msg.sender] != 0);
    require(msg.sender == gravatars[ownerToGravatar[msg.sender]].owner);

    uint id = ownerToGravatar[msg.sender];

    gravatars[id].wiki =  _wiki;
    emit UpdatedGravatar(id, msg.sender, gravatars[id].displayName, gravatars[id].imageUrl, _wiki);
  }

  // the gravatar at position 0 of gravatars[]
  // is fake
  // it's a mythical gravatar
  // that doesn't really exist
  // invoke this function once when this contract is deployed
  // but then no more
  function setMythicalGravatar() public {
    require(msg.sender == 0x2b382ede0ddcffd6e491ef4a679bf0f2ce75a611);
    gravatars.push(Gravatar(0x0, " ", " "," "));
  }
}
